import React, { useState } from 'react';
import ProductForm from './components/ProductForm';
import OrderSummary from './components/OrderSummary';

function App() {
  const [products] = useState([
    { _id: 1, name: 'Product A', price: 100 },
    { _id: 2, name: 'Product B', price: 200 },
  ]);

  const [order, setOrder] = useState([]);

  const addProductToOrder = (item) => {
    const product = products.find(p => p._id === parseInt(item.product));
    setOrder([...order, { product, quantity: item.quantity }]);
  };

  return (
    <div className="App">
      <h1>POS System</h1>
      <ProductForm products={products} onAddProduct={addProductToOrder} />
      <OrderSummary order={order} />
    </div>
  );
}

export default App;
