import React from 'react';

const OrderSummary = ({ order }) => {
  const calculateTotal = () => {
    return order.reduce(
      (total, item) => total + item.product.price * item.quantity - (item.discount || 0),
      0
    );
  };

  return (
    <div className="order-summary">
      <h3>Order Summary</h3>
      <ul>
        {order.map((item, index) => (
          <li key={index}>
            {item.product.name} - {item.quantity} pcs - Offer: {item.offer}
          </li>
        ))}
      </ul>
      <h4>Total: ${calculateTotal()}</h4>
    </div>
  );
};

export default OrderSummary;
