import React from 'react';

const OrderSummary = ({ order }) => {
  const calculateTotal = () => {
    return order.reduce(
      (total, item) =>
        total + item.product.price * item.quantity - (item.discount || 0),
      0
    );
  };

  return (
    <div className="order-summary">
      <h3>Order Summary</h3>
      <ul>
        {order.map((item, index) => (
          <li key={index}>
            {item.product.name} - {item.quantity} pcs - Offer:{" "}
            {item.product.offers.length > 0 ? (
              <>
                {item.product.offers[0].type === "flat" && (
                  <span>Flat ${item.product.offers[0].discountValue} off</span>
                )}
                {item.product.offers[0].type === "percentage" && (
                  <span>
                    {item.product.offers[0].discountValue}% off
                  </span>
                )}
                {item.product.offers[0].type === "product" && (
                  <span>Buy one get a free product</span>
                )}
              </>
            ) : (
              <span>No offer</span>
            )}
          </li>
        ))}
      </ul>
      <h4>Total: Rs{calculateTotal()}</h4>
    </div>
  );
};

export default OrderSummary;
